import React, {useState} from "react";
import InputForm from "../component/InputForm";
import ActionButton from "../component/ActionButton";
import Header from "../component/Header";
import axios from "axios";

function AddSentence() {
    const [content, setSentenceContent] = useState("")
    const [writer, setSentenceWriter] = useState("")

    function addAction() {
        // alert("testValue : " + content + ", writer : " + writer)
        const url = "http://10.20.10.111:6501/mem/sendAuthNum.dmfap";
        axios.post(url, {
            "content" : content,
            "writer": writer
        }, {
            headers: {
                "Content-Type": "application/json",
                "osType": "W",
                "contentInfo": "AdminWebCall"
            }
        }).then((response) => {
            if(response.data.code === "0000") {
                setInputState(true)

                alert("인증번호 발송이 완료되었습니다.")
            } else {
                alert("log :: " + response.data.msg)
            }
        })
            .catch((response) => {console.log('Error!') });
    }

    return (
        <div>
            <Header title="문장 추가하기" subTitle="신규 문장 추가하기"/>
            <ul>
                <InputForm title="문장 입력" inputTxt={setSentenceContent}/>
                <InputForm title="문장 작성자 입력" inputTxt={setSentenceWriter}/>
            </ul>

            <ActionButton btnTitle="추가하기" action={addAction}/>
        </div>
    )
}

export default AddSentence;
