import React from "react";
import axios from "axios";
import {ApiType} from "./DefineManager";

const apiMode = "LOCAL"
export function postCall(urlPath: String, dataBody: any, act: any, dispatchAct: React.Dispatch<any>) {
    var callUrl = ApiType[apiMode] + urlPath
    axios.post(callUrl, dataBody, {
        headers: {
            "Content-Type": "application/json",
            "osType": "W",
            "contentInfo": "AdminWebCall"
        }
    }).then((response) => {
        if(response.data.code === "0000") {
            // props.successAct(response.data.body)
            if(act != null) {
                act()
            } else {
                dispatchAct(response.data.body)
            }
        } else {
            alert("log :: " + response.data.msg)
        }
    })
        .catch((response) => {console.log('Error!') });
}
